import React from 'react';
import {Route} from 'react-router-dom'
import CollectionPage from '../collection/collection.component'
import {createStructuredSelector } from 'reselect'
import { identifier } from '@babel/types';
import {connect} from 'react-redux';
import {fetchCollectionsStartAsync} from '../../redux/shop/shop.actions'
import CollectionOverview from '../../components/collections-overview/collections-overview.component'
import {firestore,convertCollectionsSnapshotToMap} from '../../firebase/firebase.utils'    
import {selectIsCollectionFetching,selectCollectionIsLoaded} from  '../../redux/shop/shop.selector'
import WithSpinner from '../../components/with-spinner/with-spinner.component'
import CollectionPreview from '../../components/collection-preview/collection-preview.component';
import CollectionsOverviewContainer from '../../components/collections-overview/collections-overview.container'
import ColelctionsContainer from '../../pages/collection/collection.container'
const CollectionOverviewWithSpinner =  WithSpinner(CollectionOverview); 
const CollectionPageWithSpinner = WithSpinner(CollectionPage);

class ShopPage extends React.Component
    {   


        componentDidMount(){
           const{fetchCollectionsStartAsync} = this.props;
            fetchCollectionsStartAsync(); 

        }

        render(){

            const { match,isCollectionFetching,isCollectionLoaded } = this.props;  
            return (
                <div className = "shop-page">

                <Route exact path = {`${match.path}`} 
                render = {(props) => 
                <CollectionsOverviewContainer {...props}/>
                }/>
                <Route path = {`${match.path}/:collectionId`} 
                render = {(props) => 
                <CollectionPageWithSpinner isLoading = {!isCollectionLoaded } {...props}/>
                }/>

                </div>
            )
        }   

    }

    const mapStateToProps = createStructuredSelector({
        isCollectionFetching: selectIsCollectionFetching,
        isCollectionLoaded: selectCollectionIsLoaded
    })
    const mapDispatchToProps = dispatch =>({
        fetchCollectionsStartAsync: () => dispatch(fetchCollectionsStartAsync()) 
    })

export default connect(mapStateToProps ,mapDispatchToProps)(ShopPage)
