const INITIAL_STATE = {
    currentUser: null
}
//state is current state
//it is default parameter
const userReducer = (state = INITIAL_STATE,action)=>{
    switch(action.type){
        case 'SET_CURRENT_USER':
            return{
                ...state,
                currentUser: action.payload
            } 
        default: 
            return state;
    }
}
export default userReducer;
